+        <h2>👀 Total Views</h2>
+        <p id="totalViews">0</p>
+      </div>
+      <div class="stat-card">
+        <h2>❤️ Total Likes</h2>
+        <p id="totalLikes">0</p>
+      </div>
+    </div>
+
+    <h2 style="margin-top:18px;">📈 Recent Activity</h2>
+    <ul id="activityLog" class="small"></ul>
+
+    <h2 style="margin-top:18px;">🎥 Recent Uploaded Videos</h2>
+    <div id="videoList" class="video-list"></div>
+  </div>
+
