commits.content:
body {
  background: #0b0b0b;
  color: #eee;
  font-family: system-ui, -apple-system, Segoe UI, Roboto, "Helvetica Neue", Arial;
  margin: 0;
  padding: 20px;
  line-height: 1.5;
}

/* Center Content */
.container {
  max-width: 960px;
  margin: 0 auto;
}

/* Menu Buttons */
.menu {
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
}

.menu button {
  background: linear-gradient(90deg, #ff0050, #ff7a00);
  color: #fff;
  border: none;
  padding: 10px 16px;
  border-radius: 8px;
  cursor: pointer;
  font-weight: bold;
  box-shadow: 0 4px 10px rgba(255, 0, 80, 0.3);
  transition: transform 0.2s ease, box-shadow 0.3s ease;
}

.menu button:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 15px rgba(255, 0, 80, 0.4);
}

/* Headings */
h1 {
  margin: 10px 0 20px;
  font-size: 26px;
  color: #ff0050;
  text-shadow: 0 0 6px rgba(255, 0, 80, 0.8);
}

/* Upload Form */
form {
  display: flex;
  gap: 10px;
  align-items: center;
  margin-bottom: 20px;
  background: #1a1a1a;
  padding: 14px;
  border-radius: 10px;
  box-shadow: 0 0 12px rgba(255, 0, 80, 0.2);
}

input[type="file"] {
  color: #fff;
}

#status {
  margin-left: 10px;
  font-size: 14px;
  color: #00f2ea;
  font-weight: bold;
}

/* Video Feed */
#videoFeed {
  display: flex;
  flex-wrap: wrap;
  gap: 15px;
  justify-content: center;
}

video {
  background: #000;
  border-radius: 12px;
  box-shadow: 0 0 15px rgba(0, 242, 234, 0.4);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

video:hover {
  transform: scale(1.05);
  box-shadow: 0 0 25px rgba(255, 0, 80, 0.5);
}

/* Progress Bar */
.progress {
  height: 10px;
  background: #222;
  border-radius: 6px;
  overflow: hidden;
  width: 300px;
}

.progress > i {
  display: block;
  height: 100%;
  width: 0%;
  background: linear-gradient(90deg, #06b6d4, #7c3aed);
  box-shadow: 0 0 12px rgba(124, 58, 237, 0.6);
}

/* Small Text */
.small {
  font-size: 13px;
  color: #bbb;
  margin-top: 6px;
}
/* Dashboard Stats */
.stats {
  display: flex;
  gap: 20px;
  margin-top: 20px;
}

.stat-card {
  flex: 1;
  background: linear-gradient(135deg, #ff0050, #00f2ea);
  color: white;
  padding: 20px;
  border-radius: 12px;
  text-align: center;
  box-shadow: 0px 4px 20px rgba(0,0,0,0.4);
}

.stat-card h2 {
  font-size: 18px;
  margin-bottom: 10px;
}

.stat-card p {
  font-size: 28px;
  font-weight: bold;
}
